INSERT INTO `wp_options` VALUES("128", "_transient_dash_4077549d03da2e451c8b5f002294ff51", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2015/05/wordpress-4-2-2/\'>WordPress 4.2.2 Security and Maintenance Release</a> <span class=\"rss-date\">May 7, 2015</span><div class=\"rssSummary\">WordPress 4.2.2 is now available. This is a critical security release for all previous versions and we strongly encourage you to update your sites immediately. Version 4.2.2 addresses two security issues: The Genericons icon font package, which is used in a number of popular themes and plugins, contained an HTML file vulnerable to a cross-site [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://poststatus.com/announcing-wordcamp-europe-2016/\'>Post Status: Announcing: WordCamp Europe 2016</a></li><li><a class=\'rsswidget\' href=\'http://ma.tt/2015/06/celebrating-10-years-of-wordpress-com-automattic-wordpress-com-news/\'>Matt: Celebrating 10 Years</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/get-wordpress-notifications-as-a-daily-or-weekly-digest\'>WPTavern: Get WordPress Notifications as a Daily or Weekly Digest</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/ml-slider/\' class=\'dashboard-news-plugin-link\'>Meta Slider</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=ml-slider&amp;_wpnonce=6bc150aaf2&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'Meta Slider\'>Install</a>)</span></li></ul></div>", "no");
